export type Result<T> = {
    success: boolean;
} & ({
    success: true;
    data: T;
} | {
    success: false;
    error: string;
});
export declare const isError: <T>(result: Result<T>) => result is {
    success: false;
    error: string;
};
export declare const isSuccess: <T>(result: Result<T>) => result is {
    success: true;
    data: T;
};
export declare class ResultObject {
    static success<T>(data: T): Result<T>;
    static error<T>(error: string): Result<T>;
}
